P = (V + reflect(A,B)*(V))/2;

draw((-8,0)--(8,0));
draw((0,-4)--(0,5));
draw(A--B,red);
draw(V--P,dashed);
draw(C--V,Arrow(6));
draw(C--P,Arrow(6));

label("$\begin{pmatrix} \frac{16}{5} \\ \frac{8}{5} \end{pmatrix}$", P, NW);

dot("$(6,1)$", V, E);
dot("$(1,3)$", C, NW);
[/asy]

