pair A, B, C;

A = 4*dir(60);
B = (0,0);
C = (2,0);

draw(A--B--C--cycle);
draw(rightanglemark(A,C,B,10));

label("$A$", A, N);
label("$B$", B, SW);
label("$C$", C, SE);
label("$4$", (A + B)/2, NW);
[/asy]

