label("$A$", A, dir(90));
label("$B$", B, SW);
label("$C$", C, SE);
dot("$G_1$", G[1], S);
dot("$G_2$", G[2], SE);
dot("$G_3$", G[3], NW);
label("$P$", P, S);
[/asy]

