A = dir(90);
B = dir(90 + 100);
C = dir(90 - 140);
D = (A + reflect(B,C)*(A))/2;
E = -A;
O = (0,0);

draw(Circle(O,1));
draw(A--B--C--cycle);
draw(A--D);
draw(A--E);
draw(O--C);

