label("$y$", 3.2*J);
label("$z$", 3.2*K);
label("$\mathbf{v}$", V, NE);
label("$\mathbf{w}$", W, N);
label("$\mathbf{p}$", P, SW);
label("$\mathbf{r}$", R, SW);
