B = x + z;
D = y + z;
C = x + y + z;

draw((C + D)/2--(B + C)/2--(B + Y)/2--(X + Y)/2,red);
draw((X + Y)/2--(X + T)/2--(D + T)/2--(C + D)/2,red + dashed);
draw(X--Y--Z--C--D--A--cycle);
draw(B--A);
draw(B--C);
draw(B--Y);
draw(T--X,dashed);
draw(T--D,dashed);
draw(T--Z,dashed);

label("$X$", (C + D)/2, N);
label("$Y$", (B + C)/2, SE);
label("$Z$", (B + Y)/2, W);
[/asy]

